import { useContext } from "react";
import { Link } from "react-router-dom";
import { StoreContext } from "../store";
import { pageContentsSet, setActiveNavItem } from "../actions";
import { getJSON } from "../api";

export default function NavLink(props) {
   const { children, to, className, activeClassName } = props;
   const { state, dispatch } = useContext(StoreContext);

   const onClick = () => {
      pageContentsSet(dispatch, children, getJSON(to));
      setActiveNavItem(dispatch, to);
   };

   return (
      <Link to={to}>
         <div
            onClick={onClick}
            className={`
            ${className} 
            ${state.navBar.activeItem === to ? activeClassName : ""}`}
         >
            {children}
         </div>
      </Link>
   );
}
